	};
	return (
		<div className='sendMail'>
			<div className='sendMail__header'>
				<h3>New Message</h3>
				<CloseIcon className='sendMail__close' />
			</div>

			<form onSubmit={handleSubmit(onSubmit)}>
				<input
					{...register("to",{ required: true })}
					id='to'
					name='to'
